mapper.update:
UPDATE orders
SET status = #{status}
WHERE order_id = #{orderId}

mapper.select:
SELECT SUM(oi.price * oi.quantity) as total_price
FROM order_item oi
WHERE oi.order_id = #{orderId}

mapper.update:
UPDATE order_item
SET quantity = #{quantity}, price = #{price}
WHERE order_id = #{orderId} AND item_id = #{itemId}
